mapper.select:
select u.id userid,u.nickname nickname,up.truename truename, up.company cengji,up.varcharField4 zhuanye,up.varcharField3 bingqu,us.score score,us.year year, c.id courseId,c.buyable courseType,c.title courseName from user u left join user_profile up on u.id = up.id left join user_score us on up.id = us.userId left join course c on us.courseId = c.id where 1=1
and u.id in
#{userId}
limit #{start},#{plimit}

select u.id userId,cm.courseId courseId,cm.createdTime createdTime,c.buyable courseType from user u left join course_member cm on u.id = cm.userId left join course c on cm.courseId = c.id where 1=1
and u.id in
#{userId}
limit #{start},#{plimit}
