mapper.select:
select concat(u.id,'') userid,u.nickname nickname,up.truename truename, up.company cengji,up.varcharField4 zhuanye,up.varcharField3 bingqu,concat(us.score,'') score,us.year year, concat(c.id,'') courseId,concat(c.buyable,'') courseType,concat(us.courseType,'') acourseType,c.title courseName from user u left join user_profile up on u.id = up.id left join user_score us on up.id = us.userId left join course c on us.courseId = c.id where 1=1
and u.id in
#{userId}
limit #{start},#{plimit}

select concat(u.id,'') userId,concat(cm.courseId,'') courseId,concat(cm.createdTime,'') createdTime,concat(c.buyable,'') courseType from user u left join course_member cm on u.id = cm.userId left join course c on cm.courseId = c.id where 1=1
and u.id in
#{userId}
limit #{start},#{plimit}
